package algo.hashing;

public class RobinCarp {
	
	static int solve(String s1, int n, String s2, int m) {
		if(m < n) {
			return 0;
		}
		
		char[] a = s1.toCharArray(); 
		char[] b = s2.toCharArray();
		
		int cnt = 0;
		int i, j;
		int hash1 = a[0];
		int[] coms = new int[m];
		coms[0] = b[0];
		for(i = 1; i < n; i++) {
			hash1 += a[i];
			coms[i] = coms[i-1] + b[i];
		}
		if(coms[n-1] == hash1) {
			if(s1.equals(s2.substring(0, n))) {
				cnt++;
			}
		}
		for(i = n; i < m; i++) {
			coms[i] = coms[i-1] + b[i];
			if(coms[i] - coms[i-n] == hash1) {
				if(s1.equals(s2.substring(i-n+1,i+1))) {
					cnt++;
				}
				for(j = 0; j < n; j++) {
					if(a[j] != b[i-n+j+1]) {
						break;
					}
				}
				if(j == n) {
					cnt++;
				}
			}
		}
		return cnt;
	}

	public static void main(String[] args) {
		String s1 = "z";
		String s2 = "";
		long start = System.currentTimeMillis();
		System.out.println(solve(s1, s1.length(), s2, s2.length())+"-"+(System.currentTimeMillis()-start)+"ms");
	}

}
